import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

import auth from '../../services/auth';

const LoginForm = () => {
    const [loginData, setLoginData] = useState({ username: '', password: '' });
    const [error, setError] = useState('');
    const history = useHistory();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginData({ ...loginData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await auth.login(loginData.username, loginData.password);
            history.push('/tarefas');
        } catch (err) {
            setError('Login falhou. Verifique suas credenciais.');
        }
    };

    return (
        <div className="min-h-[70vh] flex items-center justify-center">
            <div className="w-full max-w-4xl bg-[#18191c] rounded-3xl shadow-2xl flex overflow-hidden">
                {/* Lado esquerdo - formulário de login */}
                <div className="flex-1 flex flex-col justify-center items-center bg-[#23242a] p-10">
                    <h2 className="text-4xl font-bold text-white mb-8">Fazer login</h2>
                    {error && <p className="text-red-400 text-center mb-2">{error}</p>}
                    <form onSubmit={handleSubmit} className="w-full max-w-xs flex flex-col gap-4">
                        <input
                            type="text"
                            id="username"
                            name="username"
                            placeholder="E-mail"
                            value={loginData.username}
                            onChange={handleChange}
                            required
                            className="bg-[#35363a] text-gray-200 placeholder-gray-400 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-violet-400"
                        />
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Senha"
                            value={loginData.password}
                            onChange={handleChange}
                            required
                            className="bg-[#35363a] text-gray-200 placeholder-gray-400 rounded-xl px-4 py-2 focus:outline-none focus:ring-2 focus:ring-violet-400"
                        />
                        <button
                            type="submit"
                            className="w-full py-2 rounded-xl bg-gradient-to-r from-violet-400 via-pink-300 to-blue-400 text-white font-semibold text-lg shadow hover:opacity-90 transition"
                        >
                            Entrar
                        </button>
                    </form>
                    <div className="flex items-center w-full max-w-xs my-6">
                        <hr className="flex-1 border-gray-600" />
                        <span className="mx-3 text-gray-400">ou</span>
                        <hr className="flex-1 border-gray-600" />
                    </div>
                    <div className="flex gap-6 justify-center">
                        <button className="bg-[#35363a] hover:bg-[#444] p-3 rounded-full text-white text-2xl shadow transition"><FaGoogle /></button>
                        <button className="bg-[#35363a] hover:bg-[#444] p-3 rounded-full text-white text-2xl shadow transition"><FaGithub /></button>
                    </div>
                </div>
                {/* Lado direito - convite para cadastro */}
                <div className="hidden md:flex flex-col justify-center items-center w-1/2 bg-gradient-to-br from-[#f8fafc] via-[#e0e6fa] to-[#c3dafe] p-10 text-center">
                    <h2 className="text-4xl font-bold text-gray-800 mb-4">Não tem<br />uma conta?</h2>
                    <p className="text-gray-600 mb-8">Lorem Ipsum has been the industry's standard dummy text ever since the 1500s</p>
                    <button
                        onClick={() => history.push('/register')}
                        className="w-64 py-2 rounded-xl border border-gray-300 bg-white/60 hover:bg-white transition text-gray-700 font-semibold text-lg shadow cursor-pointer flex items-center justify-center"
                    >
                        Cadastre-se
                    </button>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
