import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import auth from '../../services/auth';

const LoginForm = () => {
    const [loginData, setLoginData] = useState({ username: '', password: '' });
    const [error, setError] = useState('');
    const history = useHistory();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginData({ ...loginData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await auth.login(loginData.username, loginData.password);
            history.push('/tarefas');
        } catch (err) {
            setError('Login falhou. Verifique suas credenciais.');
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-[#181A20] px-2">
            <div className="w-full max-w-sm bg-white rounded-2xl shadow-2xl p-6 flex flex-col items-center">
                <div className="w-full flex flex-col items-center mb-6">
                    <div className="w-full bg-[#23242a] rounded-t-2xl rounded-b-[80px] py-8 flex flex-col items-center">
                        <h2 className="text-2xl font-bold text-white mb-2 text-center">Olá, Bem-vindo!</h2>
                        <p className="text-white text-center text-sm">Acesse o sistema para gerenciar suas tarefas</p>
                    </div>
                </div>
                <h1 className="text-2xl font-extrabold text-[#23242a] mt-4 mb-2 tracking-tight text-center">Gerenciador de Tarefas</h1>
                <h2 className="text-xl font-bold text-[#23242a] mb-4 text-center">Login</h2>
                <form onSubmit={handleSubmit} className="w-full space-y-4">
                    {error && <p className="text-red-400 text-center mb-2">{error}</p>}
                    <div>
                        <label htmlFor="username" className="block text-[#23242a] font-semibold mb-1">Login</label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            placeholder="Digite seu e-mail"
                            value={loginData.username}
                            onChange={handleChange}
                            required
                            className="w-full px-4 py-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-violet-400 placeholder-gray-400"
                        />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-[#23242a] font-semibold mb-1">Senha</label>
                        <div className="relative">
                            <input
                                type="password"
                                id="password"
                                name="password"
                                placeholder="Digite sua senha"
                                value={loginData.password}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-violet-400 placeholder-gray-400 pr-10"
                            />
                            <span className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400">
                                <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" /><path strokeLinecap="round" strokeLinejoin="round" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" /></svg>
                            </span>
                        </div>
                    </div>
                    <button type="submit" className="w-full py-3 rounded-md bg-gradient-to-r from-violet-500 to-pink-200 text-white font-semibold text-lg shadow-md transition hover:from-violet-600 hover:to-pink-300">Entrar</button>
                </form>
                <div className="flex items-center w-full my-6">
                    <div className="flex-grow h-px bg-gray-300" />
                    <span className="mx-2 text-gray-400 text-sm">Contato</span>
                    <div className="flex-grow h-px bg-gray-300" />
                </div>
                <div className="flex space-x-4 mb-2">
                    <button className="bg-[#23242a] rounded p-2"><svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M21 15a2 2 0 01-2 2H5a2 2 0 01-2-2V7a2 2 0 012-2h4l2-2 2 2h4a2 2 0 012 2z" /></svg></button>
                    <button className="bg-[#23242a] rounded p-2"><svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M21 8V7a2 2 0 00-2-2H5a2 2 0 00-2 2v1" /><path d="M3 8v10a2 2 0 002 2h14a2 2 0 002-2V8" /><path d="M16 10a4 4 0 01-8 0" /></svg></button>
                    <button className="bg-[#23242a] rounded p-2"><svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="3" /><path d="M19.4 15a1.65 1.65 0 00.33 1.82l.06.06a2 2 0 01-2.83 2.83l-.06-.06a1.65 1.65 0 00-1.82-.33 1.65 1.65 0 00-1 1.51V21a2 2 0 01-4 0v-.09A1.65 1.65 0 007 19.4a1.65 1.65 0 00-1.82.33l-.06.06a2 2 0 01-2.83-2.83l.06-.06a1.65 1.65 0 00.33-1.82A1.65 1.65 0 013 13.5V13a2 2 0 010-4v-.09A1.65 1.65 0 004.6 7a1.65 1.65 0 00-.33-1.82l-.06-.06a2 2 0 012.83-2.83l.06.06A1.65 1.65 0 007 4.6c.29-.18.62-.28 1-.28h.09A1.65 1.65 0 009.5 3h1a1.65 1.65 0 001.51 1c.29 0 .62.1.91.28a1.65 1.65 0 001.82-.33l.06-.06a2 2 0 012.83 2.83l-.06.06A1.65 1.65 0 0019.4 7c.18.29.28.62.28 1v.09A1.65 1.65 0 0021 9.5v1a1.65 1.65 0 01-1 1.51z" /></svg></button>
                </div>
                <div className="w-full text-center mt-2">
                    <span className="text-gray-600 text-sm">Não tem uma conta?</span>
                    <button onClick={() => history.push('/register')} className="ml-2 text-violet-600 font-semibold hover:underline">Registrar</button>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
