import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { register } from '../../services/auth';

const RegisterForm = () => {
    const [nome, setNome] = useState('');
    const [loginInput, setLoginInput] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErro('');
        if (!nome || !loginInput || !senha) {
            setErro('Preencha todos os campos.');
            return;
        }
        try {
            await register(nome, loginInput, senha);
            history.push('/'); // Redireciona para login após cadastro
        } catch (err) {
            setErro('Erro ao cadastrar. Tente outro login.');
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center p-2 overflow-hidden">
            <div className="w-full max-w-md bg-white rounded-3xl shadow-2xl p-8">
                <h2 className="text-3xl font-bold text-gray-900 mb-6 text-center">Cadastro</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    {erro && <p className="text-red-400 text-center mb-2">{erro}</p>}
                    <input
                        type="text"
                        placeholder="Nome completo"
                        value={nome}
                        onChange={e => setNome(e.target.value)}
                        required
                        className="w-full px-4 py-3 bg-gray-100 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-violet-400 placeholder-gray-400"
                    />
                    <input
                        type="text"
                        placeholder="Login"
                        value={loginInput}
                        onChange={e => setLoginInput(e.target.value)}
                        required
                        className="w-full px-4 py-3 bg-gray-100 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-violet-400 placeholder-gray-400"
                    />
                    <input
                        type="password"
                        placeholder="Senha"
                        value={senha}
                        onChange={e => setSenha(e.target.value)}
                        required
                        className="w-full px-4 py-3 bg-gray-100 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-violet-400 placeholder-gray-400"
                    />
                    <button type="submit" className="w-full py-3 rounded-md bg-gradient-to-r from-violet-500 to-pink-200 text-white font-semibold text-lg transition hover:from-violet-600 hover:to-pink-300">Cadastrar</button>
                </form>
                <div className="mt-6 text-center">
                    <button onClick={() => history.push('/')} className="text-violet-600 hover:underline">Já tem uma conta? Faça login</button>
                </div>
            </div>
        </div>
    );
};

export default RegisterForm;
