import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, useLocation } from 'react-router-dom';
import LoginForm from './components/Auth/LoginForm';
import RegisterForm from './components/Auth/RegisterForm';
import TarefaForm from './components/Tarefas/TarefaForm';
import TarefaList from './components/Tarefas/TarefaList';
import { getToken } from './utils/jwt';

function Layout({ children }) {
  const location = useLocation();
  const hideHeaderFooter = location.pathname === '/login' || location.pathname === '/register';
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = getToken();
    if (token) {
      setIsAuthenticated(true);
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  // Adiciona imagem de fundo e overlay de gradiente nas rotas de login e cadastro
  return (
    <div className="min-h-screen flex flex-col relative">
      {(location.pathname === '/login' || location.pathname === '/register') && (
        <>
          <img
            src="img/img (3).jpg"
            alt="Decorativo"
            className="fixed inset-0 w-full h-full object-cover z-0 pointer-events-none"
            style={{ minHeight: '100vh' }}
          />
          {/* Overlay de gradiente para reforçar a paleta */}
          <div
            className="fixed inset-0 z-10 pointer-events-none"
            style={{
              background: "linear-gradient(120deg, rgba(0,10,30,0.85) 60%, rgba(200,0,60,0.5) 90%, rgba(0,255,255,0.2) 100%)",
              minHeight: "100vh"
            }}
          ></div>
        </>
      )}
      <main className="flex-1 flex items-center justify-center p-6 z-20 relative">
        <div className={hideHeaderFooter ? 'w-full max-w-md' : 'w-full max-w-2xl'}>
          {/* Sugestão: aplique as classes abaixo nos formulários de login/cadastro para destacar */}
          {/* bg-[#0a1124]/80 border border-cyan-400 shadow-xl rounded-xl p-8 backdrop-blur-md */}
          {children}
        </div>
      </main>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Layout>
        <Switch>
          <Route path="/login" component={LoginForm} />
          <Route path="/register" component={RegisterForm} />
          <Route path="/tarefas" component={TarefaList} />
          <Route path="/nova-tarefa" component={TarefaForm} />
          <Route path="/" exact component={LoginForm} />
        </Switch>
      </Layout>
    </Router>
  );
}

export default App;
