import React, { useState } from 'react';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';

export default function AuthPage() {
  const [showLogin, setShowLogin] = useState(true);

  return (
    <div className="relative w-full max-w-2xl mx-auto">
      <div className="absolute inset-0 transition-opacity duration-500" style={{zIndex: showLogin ? 2 : 1, opacity: showLogin ? 1 : 0}}>
        <LoginForm onSwitch={() => setShowLogin(false)} />
      </div>
      <div className="absolute inset-0 transition-opacity duration-500" style={{zIndex: showLogin ? 1 : 2, opacity: showLogin ? 0 : 1}}>
        <RegisterForm onSwitch={() => setShowLogin(true)} />
      </div>
    </div>
  );
}
